import React, {useState, useEffect} from 'react'
import { Image, StyleSheet, Text, TextInput, Button, View, YellowBox } from 'react-native'
import {Supply} from './Supply'

export const Home = () =>{
    const [borrow, setBorrow] = useState(0)
    const [supply, setSupply] = useState(0)

    const balancesStyle = {
        flexDirection: "row",
        justifyContent : "space-evenly",
        width: "100%"
    }

    const balanceCol = {
        display : "flex",
        flexDirection : "column",
        alignItems: "center"
    }

    const styles = StyleSheet.create({

        balancesStyle:{
            flexDirection: "row",
            justifyContent : "space-evenly",
            width: "100%"
        },
        balanceCol: {
            display : "flex",
            flexDirection : "column",
            alignItems: "center"
        },


    })



    return(
        <View>

            {/* balances view */}
            <View style = {styles.balancesStyle}>
                <View style = {styles.balanceCol}>
                    <Text style = {{color : "#00D395"}}>Supply Balance</Text>
                    <Text>{supply}</Text>
                </View>

                <View style = {balanceCol}>
                    <Text style = {{color : "#9669ED", fontFamily : "Haas Grot Text R", fontSize: "16px"}}>Borrow Balance</Text>
                    <Text>{borrow}</Text>
                </View>
            </View>

        </View>
    )

}
