import React, {useState, useEffect} from 'react'
import { Image, StyleSheet, Text, TextInput, Button, View, YellowBox } from 'react-native'
import { useFonts } from 'expo-font';


export const Market = ({label}) =>{

    const [loaded] = useFonts({
        Neue: require('../assets/fonts/NeueHaasDisplayMediu.ttf'),
      });

      if (!loaded) {
        return null;
      }

    const styles = StyleSheet.create({    
        main:{
            backgroundColor: "white",
            flexDirection: "column",
            borderRadius: 5,
            width: "90%",
            marginTop: 30,
            fontFamily: "Neue"

        },
        labelContainer: {
            borderBottomColor: "rgba(0, 0, 0, 0.05)",
            borderBottomWidth: 1,
        },
        labelText:{
            fontSize: 18,
            fontFamily: "Neue",
            paddingTop: 15,
            paddingBottom: 15,
            paddingLeft: 20
        },
        tableHeader:{
            flexDirection: "row",
            paddingTop: 15,
            paddingBottom: 15,
            paddingLeft: 20,
            borderBottomColor: "rgba(0, 0, 0, 0.05)",
            borderBottomWidth: 1,

        },
        tableHeaderText:{
            color:"#AAB8C1"
        },
        currency:{
            flexDirection: "row",
            paddingLeft: 20,
        },
        tinyLogo:{
            width: 50,
            height: 50,

        },
        assetDesc:{
            flexDirection: "column"
        }
    })
    return(
        <View style = {styles.main}>
            <View style = {styles.labelContainer}>
                <Text style = {styles.labelText}>{label}</Text>
            </View>
            <View style = {styles.tableHeader}>
                <View style = {{flex: 2}}>
                    <Text style = {styles.tableHeaderText}>Asset</Text>
                </View>
                <View style = {{flex: 1}}>
                    <Text style = {styles.tableHeaderText}>Wallet</Text>
                </View>
                <View style = {{flex: 1}}>
                    <Text style = {styles.tableHeaderText}>Collateral</Text>
                </View>
            </View>
            <View style = {styles.currency}>
                {/* asset */}
                <View style = {{flexDirection:"row", flex: 2}}>
                    <Image style = {styles.tinyLogo} source={require("../assets/black-wallet-rings.png")}></Image>
                    <View style = {styles.assetDesc}>
                        <Text>Celo</Text>
                        <Text>6%</Text>
                    </View>

                </View>

                {/* wallet */}
                <View style = {{flex: 1}}>

                </View>

                {/* collateral */}
                <View style = {{flex: 1}}>

                </View>


            </View>
        </View>
    )
}
